$(document).ready((function(){$("#cart_form").submit((function(event){event.preventDefault();let formData=$(this).serialize();$.ajax({url:$(this).attr("action"),type:"POST",data:formData,success:function(response){response.success?($("#cart_form").hide(),$("#order_confirmed").show(),CartItemCount()):toastr.error(response.error||"An error occurred while confirming the order.")},error:function(xhr){toastr.error(xhr.responseJSON?.error||"An error occurred while confirming the order.")}})}))}));