function CartItemCount(){let url=$("#desktopCartButton").data("url");$.ajax({url:url,type:"POST",data:{_token:$('meta[name="csrf-token"]').attr("content")},success:function(response){$("#cartIconBox").text(response.count)},error:function(xhr){toastr.error("Error loading cart count.")}})}function AddToCart(button,quantity=1,color=null,size=null){let url=$(button).data("url");$.ajax({url:url,type:"POST",data:{_token:$('meta[name="csrf-token"]').attr("content"),color:color,size:size,quantity:quantity},success:function(response){toastr.success(response.success),CartItemCount()},error:function(xhr){toastr.error(xhr.responseJSON?.error||"An error occurred while adding the product.")}})}$(document).ready((function(){CartItemCount()}));